mapper.select:
SELECT carr.id AS id, carr.NAME AS NAME, carr.linkman AS linkman, carr.linkman_phone AS linkmanPhone, carr.legal_name AS legalName, carr.legal_id_card AS legalIdCard, carr.bus_license_front_img AS busLicenseFrontImg, carr.transport_license_front_img AS transportLicenseFrontImg, carr.bank_open_front_img AS bankOpenFrontImg, carr.settle_period, carr.settle_type AS settleType, carr.driver_mode AS driverMode, carr.trailer_mode AS trailerMode, carr.trunk_mode AS trunkMode, carr.is_invoice AS isInvoice, carr.state AS state, bind.card_type AS cardType, bind.card_name AS cardName, bind.bank_name AS bankName, bind.card_no AS cardNo FROM d_carrier carr JOIN d_carrier_driver_con con ON con.carrier_id = carr.id JOIN d_driver dri ON dri.id = con.driver_id JOIN s_bank_card_bind bind ON bind.user_id = dri.user_id
carr.name like CONCAT('%',#{name},'%')
and carr.linkman = #{linkman}
and carr.linkman_phone = #{linkmanPhone}
and bind.card_no = #{cardNo}
and carr.legal_name = #{legalName}
and carr.legal_id_card = #{legalIdCard}
and carr.is_invoice = #{isInvoice}
and carr.settle_type = #{settleType}
and carr.state = #{state}
and carr.create_user = #{createUser}
