mapper.select:
SELECT dc.id AS carrierId, dc.NAME AS businessName, ( CASE dc.type WHEN 1 THEN dc.linkman WHEN 2 THEN '' END ) AS NAME, ( CASE dc.type WHEN 1 THEN dc.linkman_phone WHEN 2 THEN '' END ) AS phone, dc.type AS type, dc.MODE AS MODE, ( CASE dc.type WHEN 1 THEN dvr.plate_no WHEN 2 THEN '' END ) AS plateNo, ( CASE dc.type WHEN 1 THEN dc.legal_id_card WHEN 2 THEN '' END ) AS idCard, ( CASE dc.type WHEN 1 THEN dvr.carry_car_num WHEN 2 THEN '' END ) AS carryCarNum, ( CASE dc.type WHEN 1 THEN dvr.occupied_car_num WHEN 2 THEN '' END ) AS occupiedCarNum, ( CASE dc.type WHEN 1 THEN dvr.running_state WHEN 2 THEN '' END ) AS runningState, ( CASE dc.type WHEN 1 THEN dd.business_state WHEN 2 THEN '' END ) AS businessState FROM d_carrier dc LEFT JOIN d_carrier_driver_con dcdc ON dcdc.carrier_id = dc.id LEFT JOIN d_driver dd ON dd.id = dcdc.driver_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id
dc.name like CONCAT('%',#{name},'%')
and dc.type = #{type}
and dc.linkman_phone = #{phone}
and dc.legal_id_card = #{idCard}
and dvr.plate_no = #{plateNo}
and dc.mode in
#{item}
and dc.state = 2 and dc.business_state = 0 and dcdc.state = 2 and dd.business_state = 0 group by dc.id order by dc.create_time desc,dc.check_time desc

SELECT count(dc.id) FROM d_carrier dc LEFT JOIN d_carrier_driver_con dcdc ON dcdc.carrier_id = dc.id LEFT JOIN d_driver dd ON dd.id = dcdc.driver_id
dd.id <> #{driverId}
and dc.id = #{carrierId} and (dd.phone = #{phone} or dd.id_card = #{idCard}) and dc.type = 2

SELECT count( dd.id ) FROM d_carrier dc LEFT JOIN (SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON dd.id = surd.user_id
dd.id <> #{driverId}
and dc.id = #{carrierId} and (dd.phone = #{phone} or dd.id_card = #{idCard}) and dc.type = 2

SELECT dc.id AS carrierId, dd.id AS driverId, ( CASE dc.type WHEN 1 THEN '' WHEN 2 THEN dc.NAME END ) AS businessName, dc.linkman AS `name`, dc.linkman_phone AS phone, dc.type AS type, dc.MODE AS MODE, ( CASE dc.type WHEN 1 THEN dvr.plate_no WHEN 2 THEN '' END ) AS plateNo, ( CASE dc.type WHEN 1 THEN dc.legal_id_card WHEN 2 THEN '' END ) AS idCard, ( CASE dc.type WHEN 1 THEN dvr.carry_car_num WHEN 2 THEN '' END ) AS carryCarNum, ( CASE dc.type WHEN 1 THEN dvr.occupied_car_num WHEN 2 THEN '' END ) AS occupiedCarNum, ( CASE dc.type WHEN 1 THEN dvr.running_state WHEN 2 THEN '' END ) AS runningState, ( CASE dc.type WHEN 1 THEN dd.business_state WHEN 2 THEN '' END ) AS businessState FROM d_carrier dc LEFT JOIN ( SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2 ) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON dd.id = surd.user_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id /*LEFT JOIN d_carrier_city_con dccc ON dccc.carrier_id = dc.id*/ LEFT JOIN s_bank_card_bind sbcb ON sbcb.user_id = dc.id
dc.name like CONCAT('%',#{name},'%')
and dc.type = #{type}
or dc.linkman_phone like CONCAT('%',#{phone},'%')
and dc.legal_id_card = #{idCard}
and dvr.plate_no = #{plateNo}
and dc.mode in
#{item}
and dc.state = 2 and dc.business_state = 0 and dd.business_state = 0 and surd.state = 2 AND sbcb.bank_name != '' AND sbcb.state = 1 group by dc.id order by dc.create_time desc,dc.check_time desc

SELECT dc.id AS carrierId, ( CASE dc.type WHEN 1 THEN '' WHEN 2 THEN dc.NAME END ) AS businessName, dc.linkman AS `name`, dc.linkman_phone AS phone, dc.type AS type, dc.MODE AS MODE, dvr.plate_no AS plateNo, dc.legal_id_card AS idCard, dvr.carry_car_num AS carryCarNum, dvr.occupied_car_num AS occupiedCarNum, dvr.running_state AS runningState, dd.business_state AS businessState FROM d_carrier dc LEFT JOIN ( SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2 ) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON dd.id = surd.user_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id LEFT JOIN d_carrier_city_con dccc ON dccc.carrier_id = dc.id LEFT JOIN s_bank_card_bind sbcb ON sbcb.user_id = dc.id
(dc.linkman like concat('%',#{keyword},'%') or dc.linkman_phone like concat('%',#{keyword},'%') or dvr.plate_no like concat('%',#{keyword},'%'))
and dc.type = #{type}
and dc.legal_id_card = #{idCard}
and dccc.city_code = #{cityCode}
and dc.mode in
#{item}
and dc.state = 2 and dc.business_state = 0 and dd.business_state = 0 and surd.state = 2 AND sbcb.bank_name != '' AND sbcb.state = 1 group by dc.id order by dc.create_time desc,dc.check_time desc
