mapper.select:
select

from s_city
where parent_code = #{code,jdbcType=VARCHAR}

SELECT NAME AS NAME, CODE AS CODE, parent_code AS parentCode FROM s_city where code in
#{item}
and level in (1,2)

SELECT t2.regionCode, t2.region, t2.provinceCode, t2.province, t2.cityCode, t2.city, c4.CODE AS areaCode, c4.NAME AS area FROM ( SELECT t1.regionCode, t1.region, t1.provinceCode, t1.province, c3.CODE AS cityCode, c3.NAME AS city FROM ( SELECT c1.CODE AS regionCode, c1.`name` AS region, c2.`code` AS provinceCode, c2.`name` AS province FROM s_city c1 JOIN s_city c2 ON c1.CODE = c2.parent_code WHERE c1.LEVEL = 0 ) t1 JOIN s_city c3 ON t1.provinceCode = c3.parent_code ) t2 JOIN s_city c4 ON t2.cityCode = c4.parent_code
and t2.regionCode=#{regionCode}
and t2.provinceCode=#{provinceCode}
and t2.cityCode=#{cityCode}
and c4.code=#{areaCode}

SELECT t1.provinceCode, t1.province, t1.cityCode, t1.city, c3.CODE AS areaCode, c3.NAME AS area FROM ( SELECT c1.CODE AS provinceCode, c1.`name` AS province, c2.CODE AS cityCode, c2.NAME AS city FROM s_city c1 JOIN s_city c2 ON c1.CODE = c2.parent_code WHERE c1.LEVEL = 1 ) t1 JOIN s_city c3 ON t1.cityCode = c3.parent_code
and t1.provinceCode=#{provinceCode}
and t1.cityCode=#{cityCode}
and c3.code=#{areaCode}

SELECT t1.provinceCode, t1.province, t1.cityCode, t1.city, c3.CODE AS areaCode, c3.NAME AS area FROM ( SELECT c1.CODE AS provinceCode, c1.`name` AS province, c2.CODE AS cityCode, c2.NAME AS city FROM s_city c1 JOIN s_city c2 ON c1.CODE = c2.parent_code WHERE c1.LEVEL = 1 ) t1 JOIN s_city c3 ON t1.cityCode = c3.parent_code
c3.code in (select area_code from s_store_city_con where store_id = #{storeId})
and t1.provinceCode=#{provinceCode}
and t1.cityCode=#{cityCode}
and c3.code=#{areaCode}

SELECT t1.provinceCode, t1.province, t1.cityCode, t1.city, c3.CODE AS areaCode, c3.NAME AS area FROM ( SELECT c1.CODE AS provinceCode, c1.`name` AS province, c2.CODE AS cityCode, c2.NAME AS city FROM s_city c1 JOIN s_city c2 ON c1.CODE = c2.parent_code WHERE c1.LEVEL = 1 ) t1 JOIN s_city c3 ON t1.cityCode = c3.parent_code
c3.code not in (select area_code from s_store_city_con)
and t1.provinceCode=#{provinceCode}
and t1.cityCode=#{cityCode}
and c3.code=#{areaCode}
