mapper.select:
SELECT id AS id, user_id AS userId, customer_no as customerNo, contact_phone AS contactPhone, contact_man AS contactMan, id_card AS idCard, source AS source, id_card_front_img AS idCardFrontImg, id_card_back_img AS idCardBackImg, create_time AS createTime, create_user_id as createUserId FROM c_customer
contact_phone = #{contactPhone}
and name = #{name}
and id_card = #{idCard}
and type = 1 and is_delete = 0

select c.id AS id, c.user_id as userId, c.customer_no as customerNo, c.NAME AS NAME, c.alias AS alias, c.source as source, c.contact_man AS contactMan, c.contact_phone AS contactPhone, c.customer_nature AS customerNature, c.create_time AS createTime, admin.name as createUserName from c_customer c left join b_admin admin on admin.id = c.create_user_id
c.customer_no = #{customerNo}
and c.name = #{name}
and c.alias = #{alias}
and c.contact_man = #{contactMan}
and c.contact_phone = #{contactPhone}
and admin.name like concat('%',#{createUserName},'%')
and c.customer_nature = #{customerNature}
and c.type = 2 and c.is_delete = 0

SELECT c.id AS id, c.user_id AS userId, c.customer_no as customerNo, c.NAME AS NAME, c.contact_man AS contactMan, c.contact_phone AS contactPhone, p.is_taxpayer AS isTaxpayer, p.is_invoice AS isInvoice, p.settle_type AS settleType, p.settle_period AS settlePeriod, b.description AS description, c.source AS source, c.register_time AS registerTime, c.create_user_id as createUserId FROM c_customer c JOIN c_customer_partner p ON p.customer_id = c.id JOIN s_bank_card_bind b ON b.user_id = c.user_id
c.name = #{name}
and c.contact_man = #{contactMan}
and c.contact_phone = #{contactPhone}
and c.social_credit_code = #{socialCreditCode}
and p.is_invoice = #{isInvoice}
and c.type = 3 and is_delete = 0

select user_id as useId, `name`, alias, contact_phone as contactPhone, contact_man as contactMan, contact_address as contactAddress from c_customer
and type = #{paramsDto.type}
and concat(name,alias,contact_phone,contact_man) like concat('%',#{paramsDto.info},'%')
