mapper.select:
SELECT dc.id AS carrierId, dd.id AS driverId, dd.real_name AS realName, dd.phone AS phone, dcdc.MODE AS MODE, dc.settle_type AS settleType, dv.plate_no AS plateNo, sbcb.card_name AS cardName, sbcb.bank_name AS bankName, sbcb.card_no AS cardNo, dd.id_card_front_img AS idCardFrontImg, dd.id_card_back_img AS idCardBackImg, dd.driver_licence_front_img AS driverLicenceFrontImg, dd.travel_licence_front_img AS travelLicenceFrontImg, dd.taxi_licence_front_img AS taxiLicenceFrontImg, dd.qualifi_cert_front_img AS qualifiCertFrontImg, dd.source AS source, dcdc.state AS state, dvr.running_state AS runningState, ba.NAME AS operator FROM d_carrier dc LEFT JOIN d_carrier_driver_con dcdc ON dcdc.carrier_id = dc.id LEFT JOIN d_driver dd ON dd.id = dcdc.driver_id LEFT JOIN s_bank_card_bind sbcb ON sbcb.user_id = dc.id LEFT JOIN d_driver_vehicle_con ddvc ON ddvc.driver_id = dd.id LEFT JOIN d_vehicle dv ON dv.id = ddvc.vehicle_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id LEFT JOIN b_admin ba ON ba.id = dd.check_user_id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone = #{phone}
and dd.id_card = #{idCard}
and dv.plate_no = #{plateNo}
and dd.identity = #{identity}
and dvr.running_state = #{runningState}
and dc.state = #{state}
and dcdc.mode = #{mode}
and dc.type = 1 and dd.identity = 0

SELECT dd.id AS driverId, dcdc.carrier_id AS carrierId, dd.real_name AS realName, dd.phone AS phone, dcdc.MODE AS MODE, dd.business_state AS businessState, dcdc.state AS state, dd.identity AS identity, dvr.plate_no AS plateNo, dvr.carry_car_num AS defaultCarryNum FROM d_driver dd LEFT JOIN d_carrier_driver_con dcdc ON dcdc.driver_id = dd.id LEFT JOIN d_carrier dc ON dc.id = dcdc.carrier_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone = #{phone}
and dd.id_card = #{idCard}
and dvr.plate_no = #{plateNo}
and dd.identity = #{identity}
and dvr.running_state = #{businessState}
and dcdc.state = #{state}
and dcdc.mode = #{mode}
and dc.carrier_id = #{carrierId} and dc.type = 2 and dc.business_state = 0

SELECT cr.id as carrierId, cr.`name` as `name`, d.id AS driverId, d.real_name AS driverName, d.phone AS driverPhone, vr.plate_no AS vehiclePlateNo, d.id_card AS idCard, vr.carry_car_num AS carryCarNum, vr.occupied_car_num AS occupiedCarNum, vr.running_state AS runningState, con.mode as mode FROM d_carrier cr LEFT JOIN d_carrier_driver_con con on con.carrier_id = cr.id LEFT JOIN d_driver d on con.driver_id = d.id LEFT JOIN d_vehicle_running vr ON vr.driver_id = d.id
and d.real_name like CONCAT('%',#{realName},'%')
and vr.plate_no = #{plateNo}
and d.phone = #{phone}
and d.id_card = #{idCard}
and d.type = 2 AND con.state = 2 AND d.business_state = 0
