SELECT dc.id AS carrierId, dd.id AS driverId, dd.real_name AS realName, dd.phone AS phone, dcdc.MODE AS MODE, dc.settle_type AS settleType, dv.plate_no AS plateNo, sbcb.card_name AS cardName, sbcb.bank_name AS bankName, sbcb.card_no AS cardNo, dd.id_card_front_img AS idCardFrontImg, dd.id_card_back_img AS idCardBackImg, dd.driver_licence_front_img AS driverLicenceFrontImg, dd.driver_licence_back_img AS driverLicenceBackImg, dd.travel_licence_front_img AS travelLicenceFrontImg, dd.taxi_licence_front_img AS taxiLicenceFrontImg, dd.qualifi_cert_front_img AS qualifiCertFrontImg, dd.source AS source, dd.business_state AS businessState, dcdc.state AS state, dvr.running_state AS runningState, dd.check_time as operatTime, ba.NAME AS operator FROM d_carrier dc LEFT JOIN d_carrier_driver_con dcdc ON dcdc.carrier_id = dc.id LEFT JOIN d_driver dd ON dd.id = dcdc.driver_id LEFT JOIN s_bank_card_bind sbcb ON sbcb.user_id = dc.id LEFT JOIN d_driver_vehicle_con ddvc ON ddvc.driver_id = dd.id LEFT JOIN d_vehicle dv ON dv.id = ddvc.vehicle_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id LEFT JOIN b_admin ba ON ba.id = dd.check_user_id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone like CONCAT('%',#{phone},'%')
and dd.id_card like CONCAT('%',#{idCard},'%')
and dv.plate_no like CONCAT('%',#{plateNo},'%')
and dd.business_state = #{businessState}
and dc.state = #{state}
and dcdc.mode = #{mode}
and dc.type = 1 and dd.identity = 0 group by dd.phone order by dc.create_time desc,dc.check_time desc

select

from d_driver
where user_id = #{userId}

SELECT dd.id AS driverId, dcdc.carrier_id AS carrierId, dd.real_name AS realName, dd.phone AS phone, dcdc.MODE AS MODE, dd.business_state AS businessState, dd.id_card as idCard, dcdc.role as role, dd.id_card_front_img as idCardFrontImg, dd.id_card_back_img as idCardBackImg, dvr.vehicle_id as vehicleId, dcdc.state AS state, dvr.running_state as runningState, dd.identity AS identity, dvr.plate_no AS plateNo, dvr.carry_car_num AS defaultCarryNum FROM d_driver dd LEFT JOIN d_carrier_driver_con dcdc ON dcdc.driver_id = dd.id LEFT JOIN d_carrier dc ON dc.id = dcdc.carrier_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone like CONCAT('%',#{phone},'%')
and dvr.plate_no like CONCAT('%',#{plateNo},'%')
and dd.identity = #{identity}
and dd.business_state = #{businessState}
and dcdc.state = #{state}
and dcdc.mode = #{mode}
and dc.id = #{carrierId} and dc.type = 2 and dc.state < 9 group by dd.id order by dd.create_time desc,dd.check_time desc

SELECT dc.id as carrierId, dc.`name` as `name`, dd.id AS driverId, dd.real_name AS driverName, dd.phone AS driverPhone, dvr.plate_no AS vehiclePlateNo, dd.id_card AS idCard, dvr.carry_car_num AS carryCarNum, dvr.occupied_car_num AS occupiedCarNum, dd.business_state AS businessState, dcdc.mode as mode FROM d_carrier dc LEFT JOIN d_carrier_driver_con dcdc on dcdc.carrier_id = dc.id LEFT JOIN d_driver dd on dcdc.driver_id = dd.id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id where dc.id = #{carrierId} and dd.type = 2 AND dc.state = 2 AND dcdc.state = 2
and dd.real_name like CONCAT('%',#{driverName},'%')
and dvr.plate_no = #{vehiclePlateNo}
and dd.phone = #{driverPhone}
and dd.id_card = #{driverIdCard}
group by dd.phone

SELECT dc.id AS carrierId, dd.id AS driverId, dd.real_name AS realName, dd.phone AS phone, surd.MODE AS MODE, dc.settle_type AS settleType, dv.plate_no AS plateNo, sbcb.card_name AS cardName, sbcb.bank_name AS bankName, sbcb.card_no AS cardNo, dd.id_card_front_img AS idCardFrontImg, dd.id_card_back_img AS idCardBackImg, dd.driver_licence_front_img AS driverLicenceFrontImg, dd.driver_licence_back_img AS driverLicenceBackImg, dd.travel_licence_front_img AS travelLicenceFrontImg, dd.taxi_licence_front_img AS taxiLicenceFrontImg, dd.qualifi_cert_front_img AS qualifiCertFrontImg, dd.source AS source, dd.business_state AS businessState, surd.state AS state, dvr.running_state AS runningState, dd.check_time AS operatTime, ba.NAME AS operator FROM d_carrier dc LEFT JOIN (SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON dd.id = surd.user_id LEFT JOIN s_bank_card_bind sbcb ON sbcb.user_id = dc.id LEFT JOIN d_driver_vehicle_con ddvc ON ddvc.driver_id = dd.id LEFT JOIN d_vehicle dv ON dv.id = ddvc.vehicle_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id LEFT JOIN b_admin ba ON ba.id = dd.check_user_id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone like CONCAT('%',#{phone},'%')
and dd.id_card like CONCAT('%',#{idCard},'%')
and dv.plate_no like CONCAT('%',#{plateNo},'%')
and dd.business_state = #{businessState}
and surd.state = #{state}
and surd.mode = #{mode}
and dc.type = 1 and dd.identity = 0 group by dd.phone order by dc.create_time desc,dc.check_time desc

SELECT dc.id AS carrierId, dc.`name` AS `name`, dd.id AS driverId, dd.real_name AS driverName, dd.phone AS driverPhone, dvr.plate_no AS vehiclePlateNo, dd.id_card AS idCard, dvr.carry_car_num AS carryCarNum, dvr.occupied_car_num AS occupiedCarNum, dd.business_state AS businessState, surd.MODE AS MODE FROM d_carrier dc LEFT JOIN ( SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2 ) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON surd.user_id = dd.id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id
dd.real_name like CONCAT('%',#{realName},'%')
and dvr.plate_no = #{plateNo}
or dd.phone like CONCAT('%',#{phone},'%')
and dd.id_card = #{idCard}
AND dc.state = 2 AND dc.business_state = 0 AND surd.state = 2 AND dc.type = 1 AND dc.mode = 3 group by dd.phone

SELECT dc.id AS carrierId, dc.`name` AS `name`, dd.id AS driverId, dd.real_name AS driverName, dd.phone AS driverPhone, dvr.plate_no AS vehiclePlateNo, dd.id_card AS idCard, dvr.carry_car_num AS carryCarNum, dvr.occupied_car_num AS occupiedCarNum, dd.business_state AS businessState, surd.MODE AS MODE FROM d_carrier dc LEFT JOIN ( SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2 ) surd ON surd.dept_id = dc.id LEFT JOIN d_driver dd ON surd.user_id = dd.id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id WHERE dc.id = #{carrierId} AND dd.type = 2 AND dc.state = 2 AND surd.state = 2
and dd.real_name like CONCAT('%',#{driverName},'%')
and dvr.plate_no = #{vehiclePlateNo}
and dd.phone = #{driverPhone}
and dd.id_card = #{driverIdCard}
group by dd.phone

SELECT dd.id AS driverId, dc.id AS carrierId, dd.real_name AS realName, dd.phone AS phone, surd.MODE AS MODE, dd.business_state AS businessState, dd.id_card AS idCard, ( CASE sr.role_name WHEN '个人司机' THEN 0 WHEN '下属司机' THEN 1 WHEN '承运商管理员' THEN 2 WHEN '承运商超级管理员' THEN 3 END ) AS role, dd.id_card_front_img AS idCardFrontImg, dd.id_card_back_img AS idCardBackImg, dvr.vehicle_id AS vehicleId, surd.state AS state, dvr.running_state AS runningState, dd.identity AS identity, dvr.plate_no AS plateNo, dvr.carry_car_num AS defaultCarryNum FROM d_driver dd LEFT JOIN ( SELECT * FROM s_user_role_dept WHERE dept_type = 2 AND user_type = 2 ) surd ON surd.user_id = dd.id LEFT JOIN d_carrier dc ON dc.id = surd.dept_id LEFT JOIN d_vehicle_running dvr ON dvr.driver_id = dd.id LEFT JOIN s_role sr ON sr.id = surd.role_id
dd.real_name like CONCAT('%',#{realName},'%')
and dd.phone like CONCAT('%',#{phone},'%')
and dvr.plate_no like CONCAT('%',#{plateNo},'%')
and dd.identity = #{identity}
and dd.business_state = #{businessState}
and surd.state = #{state}
and surd.mode = #{mode}
and dc.id = #{carrierId} and dc.type = 2 and dc.state < 9 group by dd.id order by dd.create_time desc,dd.check_time desc
