mapper.select:
SELECT d.id AS id, d.user_id AS userId, d.real_name AS realName, d.phone AS phone, d.id_card AS idCard, d.id_card_front_img AS idCardFrontImg, d.id_card_back_img AS idCardBackImg, d.driver_licence_front_img AS driverLicenceFrontImg, d.travel_licence_front_img AS travelLicenceFrontImg, d.taxi_licence_front_img AS taxiLicenceFrontImg, d.qualifi_cert_front_img AS qualifiCertFrontImg, d.MODE AS MODE, d.source AS source, d.business_state AS businessState, d.state AS state, v.plate_no AS plateNo, card.card_name AS cardName, card.bank_name AS bankName, card.card_no AS cardNo, carr.settle_type AS settleType, cou.car_num AS carNum, cou.total_income AS totalIncome FROM d_driver d LEFT JOIN d_carrier_driver_con driCon ON driCon.driver_id = d.id LEFT JOIN d_carrier carr ON carr.id = driCon.carrier_id LEFT JOIN s_bank_card_bind card ON card.user_id = d.user_id LEFT JOIN d_driver_vehicle_con con ON con.driver_id = d.id LEFT JOIN d_vehicle v ON v.id = con.vehicle_id LEFT JOIN d_driver_car_count cou ON cou.driver_id = d.id
d.real_name like CONCAT('%',#{realName},'%')
and d.phone = #{phone}
and d.id_card = #{idCard}
and v.plate_no = #{plateNo}
and d.identity = #{identity}
and d.business_state = #{businessState}
and d.state = #{state}
and d.mode = #{mode}
