mapper.select:
SELECT d.id AS id, d.user_id AS userId, d.real_name AS realName, d.phone AS phone, d.id_card AS idCard, d.id_card_front_img AS idCardFrontImg, d.id_card_back_img AS idCardBackImg, d.driver_licence_front_img AS driverLicenceFrontImg, d.travel_licence_front_img AS travelLicenceFrontImg, d.taxi_licence_front_img AS taxiLicenceFrontImg, d.qualifi_cert_front_img AS qualifiCertFrontImg, ( CASE d.MODE WHEN 0 THEN '代驾' WHEN 1 THEN '干线司机' WHEN 2 THEN '拖车司机' WHEN 4 THEN '全支持' END ) AS MODE, ( CASE d.source WHEN 1 THEN 'App注册' WHEN 2 THEN 'Applet注册' WHEN 3 THEN '业务员创建' WHEN 4 THEN '承运商管理员创建' WHEN 11 THEN '掌控接口' WHEN 12 THEN 'otm接口' END ) AS source, ( CASE d.business_state WHEN 0 THEN '营运中' WHEN 1 THEN '停运中' END ) AS businessState, ( CASE d.state WHEN 0 THEN '待审核' WHEN 2 THEN '审核通过' WHEN 4 THEN '已驳回' WHEN 7 THEN '已冻结' END ) AS state, v.plate_no AS plateNo, card.card_name AS cardName, card.bank_name AS bankName, card.card_no AS cardNo, ( CASE carr.settle_type WHEN 1 THEN '时付' WHEN 2 THEN '账期' END ) AS settleType, cou.car_num AS carNum, cou.total_income AS totalIncome FROM d_driver d LEFT JOIN d_carrier_driver_con driCon ON driCon.driver_id = d.id LEFT JOIN d_carrier carr ON carr.id = driCon.carrier_id LEFT JOIN s_bank_card_bind card ON card.user_id = d.user_id LEFT JOIN d_driver_vehicle_con con ON con.driver_id = d.id LEFT JOIN d_vehicle v ON v.id = con.vehicle_id LEFT JOIN d_driver_car_count cou ON cou.driver_id = d.id
d.real_name like CONCAT('%',#{realName},'%')
and d.phone = #{phone}
and d.id_card = #{idCard}
and v.plate_no = #{plateNo}
and d.identity = #{identity}
and d.business_state = #{businessState}
and d.state = #{state}
and d.mode = #{mode}
