mapper.select:
select DISTINCT(woc.no),woc.vin,woc.brand,woc.model,woc.order_no as orderNo,
zcity.name as largeArea,
wo.input_store_name as inputStoreName,wo.start_address as startAddress,
wo.end_address as endAddress,
woc.finish_time as deliveryDate,

wo.customer_id as customerId,
wo.customer_type as type,cc.name as customerName,
cc.pay_mode as payMode,
if(wo.customer_type=3,woc.total_fee,null) as feeShare,
if(wo.customer_type=3,woc.total_fee,(woc.total_fee-woc.coupon_offset_fee)) as freightReceivable,
if(woc.wl_pay_state=2,woc.total_fee,null) as amountReceived,
if(woc.wl_pay_state=2,(woc.pick_fee+woc.trunk_fee+woc.back_fee+woc.add_insurance_fee),0) as totalIncome,
woc.wl_pay_time as receivedTime

from w_order_car woc
left join w_order wo on woc.order_no = wo.no
left join c_customer cc on wo.customer_id = cc.id
left join w_waybill_car wwc on woc.id = wwc.order_car_id
left join w_task_car wtc on wtc.waybill_car_id = wwc.id
left join w_task wt on wtc.task_id = wt.id
left join s_city zc on zc.code = wo.start_province_code
left join s_city zcity on zcity.code = zc.parent_code


        and woc.no like concat('%',#{no},'%')


        and  woc.vin like concat('%',#{vin},'%')


        and  woc.order_no like concat('%',#{orderNo},'%')


        and woc.finish_time >= #{completeStartTime}


        and woc.finish_time <= #{completeEndTime}



        and wo.start_province_code = #{startProvinceCode}


        and wo.start_city_code = #{startCityCode}


        and wo.start_area_code = #{startAreaCode}


        and wo.end_province_code = #{endProvinceCode}


        and wo.end_city_code = #{endCityCode}


        and wo.end_area_code = #{endAreaCode}


        and zcity.code = #{area}


        and cc.type = #{type}


        and  cc.name  like concat('%',#{customerName},'%')


        and  cc.pay_mode =#{payMode}



        and woc.brand = #{brand}


        and woc.model = #{model}


        and wo.input_store_name = #{inputStoreName}



        and woc.wl_pay_state = #{state}


        and woc.wl_pay_time >= #{receiveStartTime}


        and woc.wl_pay_time <= #{receiveEndTime}


group by woc.no
order by woc.finish_time desc

select DISTINCT(woc.no)
,woc.vin,woc.brand,woc.model,woc.order_no as orderNo,
zcity.name as largeArea,
wo.input_store_name as inputStoreName,wo.start_address as startAddress,
wo.end_address as endAddress,
woc.finish_time as deliveryDate,

wo.customer_id as customerId,
wo.customer_type as type,cc.name as customerName,
cc.pay_mode as payMode,
if(wo.customer_type=3,woc.total_fee,null) as feeShare,
(woc.pick_fee+woc.trunk_fee+woc.back_fee+woc.add_insurance_fee) as freightReceivable,
if(woc.wl_pay_state=2,woc.total_fee,null) as amountReceived,
if(woc.wl_pay_state=2,(woc.pick_fee+woc.trunk_fee+woc.back_fee+woc.add_insurance_fee),0) as totalIncome,
woc.wl_pay_time as receivedTime

from w_order_car woc
left join w_order wo on woc.order_no = wo.no
left join c_customer cc on wo.customer_id = cc.id
left join w_waybill_car wwc on woc.id = wwc.order_car_id
left join w_task_car wtc on wtc.waybill_car_id = wwc.id
left join w_task wt on wtc.task_id = wt.id
left join s_city zc on zc.code = wo.start_province_code
left join s_city zcity on zcity.code = zc.parent_code


        and woc.no like concat('%',#{no},'%')


        and  woc.vin like concat('%',#{vin},'%')


        and  woc.order_no like concat('%',#{orderNo},'%')


        and woc.finish_time >= #{completeStartTime}


        and woc.finish_time <= #{completeEndTime}



        and wo.start_province_code = #{startProvinceCode}


        and wo.start_city_code = #{startCityCode}


        and wo.start_area_code = #{startAreaCode}


        and wo.end_province_code = #{endProvinceCode}


        and wo.end_city_code = #{endCityCode}


        and wo.end_area_code = #{endAreaCode}


        and zcity.code = #{area}


        and cc.type = #{type}


        and  cc.name  like concat('%',#{customerName},'%')


        and  cc.pay_mode =#{payMode}



        and woc.brand = #{brand}


        and woc.model = #{model}


        and wo.input_store_name = #{inputStoreName}



        and woc.wl_pay_state = #{state}


        and woc.wl_pay_time >= #{receiveStartTime}


        and woc.wl_pay_time <= #{receiveEndTime}


group by woc.no
order by woc.finish_time desc

select woc.no,woc.vin,woc.brand,woc.model,cc.id,cc.type,
cc.pay_mode as payMode,
if(cc.pay_mode=0,'时付','账期') as payModeName,
wwc.freight_fee as freightReceivable,
wo.start_province,wo.start_city,
wo.start_area,wo.end_province,wo.end_city,wo.end_area,
woc.order_no,woc.now_area_code,wo.input_store_id,
wo.input_store_name,wt.complete_time,
cc.contact_phone as contactPhone,
wo.customer_name as customerName
from w_order_car woc
left join w_order wo on woc.order_id = wo.id
left join c_customer cc on cc.id = wo.customer_id
left join w_waybill_car wwc on wwc.order_car_no = woc.no
left join w_task wt on wt.waybill_no = wwc.waybill_no

where cc.pay_mode=1

    and woc.no like concat('%',#{no},'%')


    and  woc.vin = #{vin}


    and  woc.order_no like concat('%',#{orderNo},'%')


    and wt.complete_time >= #{completeStartTime}


    and wt.complete_time <= #{completeEndTime}



    and wo.start_province_code = #{startProvinceCode}


    and wo.start_city_code = #{startCityCode}


    and wo.start_area_code = #{startAreaCode}


    and wo.end_province_code = #{endProvinceCode}


    and wo.end_city_code = #{endCityCode}


    and wo.end_area_code = #{endAreaCode}



    and cc.type = #{type}


    and  cc.name  like concat('%',#{customerName},'%')


    and  cc.pay_mode =#{payMode}



    and cc.brand = #{brand}


    and cc.model = #{model}


    and wo.input_store_name = #{inputStoreName}


order by wo.create_time desc

select ww.no as waybillNo,ww.complete_time as completeTime, ww.freight_fee as freightFee,ww.type, if(dc.settle_type=0,'时付','账期') as settleType, ww.guide_line as guideLine,ww.carrier_id, ww.carrier_name as carrierName, wt.driver_name as driverName, wt.driver_phone as driverPhone, wt.vehicle_plate_no as vehiclePlateNo from w_waybill ww left join d_carrier dc on dc.id=ww.carrier_id left join w_task wt on wt.waybill_id = ww.id where dc.settle_type=0 and ww.freight_pay_state=1
and ww.no like concat('%',#{waybillNo},'%')
and ww.complete_time >= #{completeStartTime}
and ww.complete_time <= #{completeEndTime}
and ww.type =#{waybillType}
and ww.carrier_name like concat('%',#{carrierName},'%')
and wt.driver_name like concat('%',#{driverName},'%')
and wt.driver_phone like concat('%',#{driverPhone},'%')
and wt.vehicle_plate_no like concat('%',#{vehiclePlateNo},'%')
order by ww.create_time desc

select ww.id as waybillId,ww.no as waybillNo,ww.complete_time as completeTime, ww.freight_fee as freightFee,ww.type, if(dc.settle_type=0,'时付','账期') as settleType, ww.guide_line as guideLine,ww.carrier_id, ww.carrier_name as carrierName, wt.driver_name as driverName, wt.driver_phone as driverPhone, wt.vehicle_plate_no as vehiclePlateNo, if(fep.state=0,'未付款','已付款') as state, fep.operator from f_external_payment fep left join w_waybill ww on fep.waybill_id = ww.id left join d_carrier dc on dc.id=ww.carrier_id left join w_task wt on wt.waybill_id = ww.id where dc.settle_type=0
and ww.no like concat('%',#{waybillNo},'%')
and ww.complete_time >= #{completeStartTime}
and ww.complete_time <= #{completeEndTime}
and ww.type =#{waybillType}
and fep.state =#{state}
and ww.carrier_name like concat('%',#{carrierName},'%')
and wt.driver_name like concat('%',#{driverName},'%')
and wt.driver_phone like concat('%',#{driverPhone},'%')
and wt.vehicle_plate_no like concat('%',#{vehiclePlateNo},'%')
and fep.operator like concat('%',#{operator},'%')
order by ww.create_time desc

select DISTINCT(ww.id) as waybillId,ww.no as waybillNo,ww.complete_time as completeTime, ww.freight_fee as freightFee,ww.type, if(dc.settle_type=0,'时付','账期') as settleType, ww.guide_line as guideLine,ww.carrier_id, ww.carrier_name as carrierName, wt.driver_name as driverName, wt.driver_phone as driverPhone, ( case when ww.freight_pay_state=0 then '未付款' when ww.freight_pay_state=1 then '已付款' when ww.freight_pay_state=2 then '正在支付' when ww.freight_pay_state=-1 then '支付失败' when ww.freight_pay_state=-2 then '支付失败' end ) as state, wt.vehicle_plate_no as vehiclePlateNo, sbcb.card_name as cardName, sbcb.card_no as cardNo, sbcb.bank_name as bankName, fep.operator from w_waybill ww left join d_carrier dc on dc.id=ww.carrier_id left join w_task wt on wt.waybill_id = ww.id left join f_external_payment fep on fep.waybill_id=ww.id left join s_bank_card_bind sbcb on sbcb.user_id = ww.carrier_id where dc.settle_type=0 and ww.state=100
and ww.no like concat('%',#{waybillNo},'%')
and ww.complete_time >= #{completeStartTime}
and ww.complete_time <= #{completeEndTime}
and ww.type =#{waybillType}
and ww.freight_pay_state =#{state}
and ww.carrier_name like concat('%',#{carrierName},'%')
and wt.driver_name like concat('%',#{driverName},'%')
and wt.driver_phone like concat('%',#{driverPhone},'%')
and wt.vehicle_plate_no like concat('%',#{vehiclePlateNo},'%')
and fep.operator like concat('%',#{operator},'%')
order by ww.complete_time desc

select ww.no as wayBillNo,ww.type as wayBillType from w_waybill ww
and ww.no like concat('%',#{wayBillNo},'%')
and ww.type = #{wayBillType}

select wt.no,wt.complete_time as completeTime, ww.type as waybillType, ww.carrier_id as carrierId, ww.carrier_type as carrierType, ww.carrier_name as carrierName, wt.driver_id as driverId, wt.driver_name as driverName, wt.driver_phone as driverPhone, wt.vehicle_plate_no as vehiclePlateNo, if(dc.settle_type=0,'时付','账期') as settleTypeName, wt.car_num as carNum, dc.settle_period as settlePeriod from w_task wt left join w_waybill ww on ww.no = wt.waybill_no left join d_carrier dc on dc.id = ww.carrier_id where dc.settle_type = 1 and wt.complete_time > 0 and carrier_type in (1,2,4,5) and wt.no not in (select IFNULL(fpd.no,'') from f_payable_detail fpd)
and wt.no like concat('%',#{waybillNo},'%')
and ww.carrier_name like concat('%',#{carrierName},'%')
and wt.driver_name like concat('%',#{driverName},'%')
and wt.complete_time >=#{completeStartTime}
and wt.complete_time <=#{completeEndTime}
and wt.driver_phone like concat('%',#{driverPhone},'%')
and wt.vehicle_plate_no like concat('%',#{vehiclePlateNo},'%')
and ww.type = #{waybillType}
order by wt.complete_time desc

select fp.invoice_no as invoiceNo, fp.serial_number as serialNumber, fp.freight_fee as freightFee, fp.apply_time as applyTime, fp.applicant_id as applicantId, ba.name as applicant from f_payable fp left join b_admin ba on ba.id = fp.applicant_id where fp.state='0'
and fp.serial_number like concat('%',#{serialNumber},'%')
and fp.apply_time >=#{applyStartTime}
and fp.apply_time <=#{applyEndTime}
and ba.name like concat('%',#{applicant},'%')
order by fp.apply_time desc

select fp.serial_number as serialNumber, fp.freight_fee as freightFee, fp.invoice_no as invoiceNo, fp.apply_time as applyTime, fp.applicant_id as applicantId, fp.confirm_id as confirmId, fp.confirm_time as confirmTime, ba.name as applicant, bc.name as confirmMan from f_payable fp left join b_admin ba on ba.id = fp.applicant_id left join b_admin bc on bc.id = fp.confirm_id where fp.state='1'
and fp.serial_number like concat('%',#{serialNumber},'%')
and fp.apply_time >=#{applyStartTime}
and fp.apply_time <=#{applyEndTime}
and ba.name like concat('%',#{applicant},'%')
and fp.confirm_time >=#{confirmStartTime}
and fp.confirm_time <=#{confirmEndTime}
and ba.name like concat('%',#{confirmMan},'%')
and fp.invoice_no like concat('%',#{invoiceNo},'%')
order by fp.confirm_time desc

select invoice_no as invoiceNo,total_freight_paid as totalFreightPay,
writeoff_time as writeoffTime
from f_payable where serial_number=#{serialNumber}

select fp.serial_number as serialNumber, fp.freight_fee as freightFee, fp.invoice_no as invoiceNo, fp.apply_time as applyTime, fp.applicant_id as applicantId, fp.confirm_id as confirmId, fp.confirm_time as confirmTime, fp.writeoff_id as writeoffId, fp.writeoff_time as writeoffTime, fp.total_freight_paid as totalFreightPay, (fp.freight_fee-fp.total_freight_paid) as difference, ba.name as applicant, bc.name as confirmMan, bd.name as writeOffMan from f_payable fp left join b_admin ba on ba.id = fp.applicant_id left join b_admin bc on bc.id = fp.confirm_id left join b_admin bd on bd.id = fp.writeoff_id where fp.state='2'
and fp.serial_number like concat('%',#{serialNumber},'%')
and fp.apply_time >=#{applyStartTime}
and fp.apply_time <=#{applyEndTime}
and ba.name like concat('%',#{applicant},'%')
and fp.confirm_time >=#{confirmStartTime}
and fp.confirm_time <=#{confirmEndTime}
and bc.name like concat('%',#{confirmMan},'%')
and fp.writeoff_time >=#{writeOffStartTime}
and fp.writeoff_time <=#{writeOffEndTime}
and bd.name like concat('%',#{writeOffMan},'%')
and fp.invoice_no lifp.writeoff_timeke concat('%',#{invoiceNo},'%')
order by fp.writeoff_time desc

select wo.id as orderId,wo.no as orderNo, wo.customer_id as customerId, wo.customer_name as customerName, ( case when wo.flag=0 then '未付款' when wo.flag=1 then '付款中' when wo.flag=2 then '已付款' when wo.flag=-2 then '支付失败' end ) as state, wo.service_fee_pay_time as payTime, wo.start_city as startCity, wo.end_city as endCity, ( case when wo.pay_type=0 then '到付' when wo.pay_type=1 then '预付' when wo.pay_type=2 then '账期' end ) as customerPayType, wo.wl_pay_time as wlPayTime, wo.total_fee as totalFee, wo.finish_time as completeTime, if(ccp.settle_type=0,'时付','账期') as settleType, if(sbcb.card_type=1,'公户','私户') as cardType, sbcb.bank_code,sbcb.bank_name as bankName, sbcb.card_name as cardName, sbcb.card_no as cardNo, sbcb.id_card as IDCard from w_order wo left join s_bank_card_bind sbcb on wo.customer_id = sbcb.user_id left join c_customer_partner ccp on ccp.customer_id = wo.customer_id where wo.state=100 and wo.customer_type=3
and wo.no like concat('%',#{orderNo},'%')
and wo.service_fee_pay_time >=#{startPayTime}
and wo.service_fee_pay_time <=#{endPayTime}
and wo.finish_time >=#{startCompleteTime}
and wo.finish_time <=#{endCompleteTime}
and wo.flag = #{state}
wo.customer_name like concat('%',#{customerName},'%')
order by wo.service_fee_pay_time desc
