mapper.select:
select fia.serial_number as serialNumber,wwc.freight_fee as freightreceivable,fia.amount as amount, cci.type,cci.name as customerName, cci.tax_code as taxPayerNumber,cci.tel ,cci.invoice_address as address,cci.bank_name as bankName,cci.bank_account as bankAccout, cci.pickup_person as pickUpPerson,cci.pickup_phone as pickUpPhone,cci.pickup_address as pickUpAddress, fia.invoice_time as applyTime,fia.invoice_man as applicant, fia.confirm_time as confirmTime,fia.confirm_man as confirmMan,fia.writeoff_time as writeOffTime,fia.writeoff_man as writeOffMan from f_invoice_apply fia left join w_waybill_car wwc on fia.order_car_no = wwc.order_car_no left join c_customer_invoice cci on fia.invoice_id = cci.id where fia.state=1
and fia.serial_number like concat('%',#{serialNumber},'%')
and cci.tax_code like concat('%',#{taxPayerNumber},'%')
and cci.name like concat('%',#{customerName},'%')
and cci.tel like concat('%',#{phone},'%')
and fia.invoice_time >= #{applyStartTime}
and fia.invoice_time <= #{applyEndTime}
and fia.invoice_man like concat('%',#{applicant},'%')
and cci.type = #{type}

select fia.serial_number as serialNumber,wwc.freight_fee as freightreceivable,fia.amount as amount, cci.type,cci.name as customerName, cci.tax_code as taxPayerNumber,cci.tel ,cci.invoice_address as address,cci.bank_name as bankName,cci.bank_account as bankAccout, cci.pickup_person as pickUpPerson,cci.pickup_phone as pickUpPhone,cci.pickup_address as pickUpAddress, fia.invoice_time as applyTime,fia.invoice_man as applicant, fia.confirm_time as confirmTime,fia.confirm_man as confirmMan,fia.writeoff_time as writeOffTime,fia.writeoff_man as writeOffMan from f_invoice_apply fia left join w_waybill_car wwc on fia.order_car_no = wwc.order_car_no left join c_customer_invoice cci on fia.invoice_id = cci.id where fia.state=2
and fia.serial_number like concat('%',#{serialNumber},'%')
and cci.tax_code like concat('%',#{taxPayerNumber},'%')
and cci.name like concat('%',#{customerName},'%')
and cci.tel like concat('%',#{phone},'%')
and fia.invoice_time >= #{applyStartTime}
and fia.invoice_time <= #{applyEndTime}
and fia.invoice_man like concat('%',#{applicant},'%')
and cci.type = #{type}
and fia.confirm_time >= #{invoiceStartTime}
and fia.confirm_time <= #{invoiceEndTime}
and fia.confirm_man like concat('%',#{confirmMan},'%')
