mapper.select:
select
o.*,
CONCAT(start_province,start_city,start_area,start_address) as startFullAddress,
CONCAT(end_province,end_city,end_area,end_address) as endFullAddress,
(sum(IFNULL(oc.pick_fee,0)) + sum(IFNULL(oc.trunk_fee,0)) + sum(IFNULL(oc.back_fee,0)) +sum(IFNULL(oc.add_insurance_fee,0))) as wlTotalFee,
(case when o.customer_type =3 then(sum(IFNULL(oc.pick_fee,0)) + sum(IFNULL(oc.trunk_fee,0)) + sum(IFNULL(oc.back_fee,0)) +sum(IFNULL(oc.add_insurance_fee,0)) + IFNULL(o.coupon_offset_fee,0))
else 0 end
) as totalAgencyFee,
s1.region as region,
s1.region_code as regionCode,
s1.detail_addr as inputStoreAddress,
s2.detail_addr as startStoreAddress,
s3.detail_addr as endStoreAddress,
ht.contract_no as contractNo,
ht.settle_period as settlePeriod
from w_order o
join w_order_car oc on oc.order_id = o.id
left join s_store s1 on o.input_store_id = s1.id
left join s_store s2 on o.start_store_id = s2.id
left join s_store s3 on o.end_store_id = s3.id
left join c_customer_contract ht on ht.id = o.customer_contract_id



            and o.state = 0


            and o.state <= 10 and o.state > 0


            and o.state = 15


            and o.state = 25


            and o.state > 25 and o.state < 100


            and o.state = 100


            and o.state = 113


            and o.state = 114







                and o.expect_start_date > #{paramsDto.beginTime}


                and o.expect_start_date < #{paramsDto.endTime}




                and o.expect_end_date > #{paramsDto.beginTime}


                and o.expect_end_date < #{paramsDto.endTime}




                and o.create_time > #{paramsDto.beginTime}


                and o.create_time < #{paramsDto.endTime}




                and o.check_time > #{paramsDto.beginTime}


                and o.check_time < #{paramsDto.endTime}




                and o.finish_time > #{paramsDto.beginTime}


                and o.finish_time < #{paramsDto.endTime}




        and o.no = #{paramsDto.no}


        and o.customer_name = #{paramsDto.customerName}


        and o.customer_type = #{paramsDto.customerType}


        and o.customer_phone = #{paramsDto.customerPhone}


        and o.input_store_id = #{paramsDto.inputStoreId}


        and o.start_province_code = #{paramsDto.startProvinceCode}


        and o.start_city_code = #{paramsDto.startCityCode}


        and o.start_area_code = #{paramsDto.startAreaCode}


        and o.start_store_id = #{paramsDto.startStoreId}


        and o.end_province_code = #{paramsDto.endProvinceCode}


        and o.end_city_code = #{paramsDto.endCityCode}


        and o.end_area_code = #{paramsDto.endAreaCode}


        and o.end_store_id = #{paramsDto.endStoreId}



        and o.pick_contact_name = #{paramsDto.pickContactName}


        and o.pick_contact_phone = #{paramsDto.pickContactPhone}


        and o.pick_type = #{paramsDto.pickType}


        and o.back_type = #{paramsDto.backType}


        and o.back_contact_name = #{paramsDto.backContactName}


        and o.back_contact_phone = #{paramsDto.backContactPhone}


        and o.line_id = #{paramsDto.lineId}


        and o.source = #{paramsDto.source}



        and o.create_user_name = #{paramsDto.createUserName}


        and o.pay_type = #{paramsDto.payType}


        and s1.region_code = #{paramsDto.regionCode}


group by o.id

select
    o.no as no,
    o.state as state,
    o.start_city as startCity,
    o.end_city as endCity,
    o.total_fee as totalFee,
    #{brand} as brand,
    #{model} as model
from
    w_order o

    customer_id = #{customerId}
    /*待确认*/

        and o.state <= 25

    /*运输中*/

        and o.state > 25
        and o.state < 100

    /*已交付*/

        and o.state = 100

    /*全部*/

        and (o.state <= 100 or o.state = 113)


        and (
        o.no like concat('%',#{key},'%')
        or o.start_city like concat('%',#{key},'%')
        or o.end_city like concat('%',#{key},'%')
        )


        and o.input_store_id = #{storeId}


        and o.start_city_code = #{startCityCode}


        and o.end_city_code = #{endCityCode}


        and o.create_time >= #{startDateMS}


        and o.create_time <= #{endDateMS}


order by o.create_time

    desc


    asc


    desc
