mapper.select:
SELECT t.id AS taskId, t.waybill_id AS waybillId, t.no AS waybillNo, t.guide_line AS guideLine, t.car_num AS carNum, t.create_time AS createTime, w.type AS type, car.expect_start_time AS expectStartTime, sum(car.freight_fee) AS freightFee FROM ( SELECT task.id, task.waybill_id, task.waybill_no, task.guide_line, task.car_num, task.state,task.create_time,tc.waybill_car_id FROM w_task task LEFT JOIN w_task_car tc ON task.id = tc.task_id
task.driver_id = #{loginId}
AND task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@WAIT_LOAD.code}
AND task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@TRANSPORTING.code}
) t LEFT JOIN w_waybill w ON t.waybill_id = w.id LEFT JOIN w_waybill_car car ON t.waybill_car_id = car.id GROUP BY t.id

select t.id as taskId, t.waybill_id as waybillId, t.no as waybillNo, t.guide_line as guideLine, t.car_num as carNum, t.complete_time as completeTime, w.type as type, sum(car.freight_fee) as freightFee from (select task.id, task.waybill_id, task.waybill_no, task.guide_line, task.car_num,task.state,task.complete_time,tc.waybill_car_id from w_task task left join w_task_car tc on task.id = tc.task_id where task.driver_id = #{loginId} and task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@FINISHED.code} ) t left join w_waybill w on t.waybill_id = w.id left join w_waybill_car car on t.waybill_car_id = car.id
and t.waybill_no like concat('%',#{waybillNo},'%')
and w.type = #{type}
and car.expect_start_date >= #{expectStartDateS}
and car.expect_start_date <= #{expectStartDateE}
and t.complete_time >= #{completeTimeS}
and t.complete_time <= #{completeTimeE}
and t.guide_line like concat('%',#{lineStart},'%')
and t.guide_line like concat('%',#{lineEnd},'%')
and t.guide_line like concat('%-',#{lineEnd},'%')
group by t.id order by t.complete_time desc

select t.id as taskId, t.waybill_id as waybillId, t.no as waybillNo, t.guide_line as guideLine, t.car_num as carNum, t.create_time as createTime, t.driver_name as driverName, t.driver_phone as driverPhone, t.vehicle_plate_no as vehiclePlateNo, w.type as type, sum(car.freight_fee) as freightFee from (select task.id, task.waybill_id, task.waybill_no, task.guide_line, task.car_num, task.state,task.complete_time,task.create_time,task.driver_name,task.driver_phone, task.vehicle_plate_no,tc.waybill_car_id from w_task task left join w_task_car tc on task.id = tc.task_id where task.create_user_id = #{loginId} ) t left join w_waybill w on t.waybill_id = w.id left join w_waybill_car car on t.waybill_car_id = car.id
and t.waybill_no like concat('%',#{waybillNo},'%')
and w.type = #{type}
and car.expect_start_date >= #{expectStartDateS}
and car.expect_start_date <= #{expectStartDateE}
and t.complete_time >= #{completeTimeS}
and t.complete_time <= #{completeTimeE}
and t.guide_line like concat('%',#{lineStart},'%')
and t.guide_line like concat('%',#{lineEnd},'%')
and t.guide_line like concat('%-',#{lineEnd},'%')
group by t.id order by t.complete_time desc

select
w.carrier_name as carrierName,
w.car_num as waybillCarNum,
w.type as waybillType,
vr.occupied_car_num as occupiedCarNum,
vr.carry_car_num as carryCarNum,
t.*
from w_waybill w
LEFT JOIN w_task t on w.id = t.waybill_id
left join d_vehicle_running vr on vr.id = t.vehicle_running_id

    w.carrier_id = #{paramsDto.carrierId}

        and w.no = #{paramsDto.waybillNo}


        and t.no = #{paramsDto.taskNo}


        and t.driver_name  = #{paramsDto.driverName}


        and t.driver_phone  = #{paramsDto.driverPhone}


        and t.vehicle_plate_no  = #{paramsDto.vehiclePlateNo}


order by t.id desc

select count(0)
from w_task_car
where task_id = #{taskId}
and waybill_car_id = #{waybillCarId}

SELECT t.id AS taskId, t.waybill_id AS waybillId, t.no AS waybillNo, t.guide_line AS guideLine, t.car_num AS carNum, t.create_time AS createTime, t.state AS taskState, w.type AS type, sum(car.freight_fee) AS freightFee FROM ( SELECT task.id, task.waybill_id, task.waybill_no, task.guide_line, task.car_num,task.state,task.create_time,tc.waybill_car_id FROM w_task task LEFT JOIN w_task_car tc ON task.id = tc.task_id
task.driver_id = #{loginId}
AND task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@WAIT_LOAD.code}
AND task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@TRANSPORTING.code}
AND (task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@WAIT_LOAD.code} OR task.state = ${@com.cjyc.common.model.enums.task.TaskStateEnum@TRANSPORTING.code})
) t LEFT JOIN w_waybill w ON t.waybill_id = w.id LEFT JOIN w_waybill_car car ON t.waybill_car_id = car.id GROUP BY t.id
