mapper.select:
       select v.id as id, v.plate_no as plateNo,v.default_carry_num as defaultCarryNum,d.real_name as realName,
				d.phone as phone,
				(case v.state when 0 then '待审核'
							  when 2 then '已审核'
							  when 4 then '已驳回'
							  when 7 then  '已停用'
 					end) as state,
			 v.create_time as createTime
       from d_driver d
			 join d_driver_vehicle_con dvc on dvc.driver_id = d.id
			 left join d_vehicle v on dvc.vehicle_id = v.id


                v.plate_no = #{plateNo}


                and  v.real_name = #{realName}


                and  v.phone = #{phone}


                and  v.state = #{state}


                and  v.ownership_type = #{ownershipType}

SELECT veh.plate_no AS plateNo, veh.default_carry_num AS defaultCarryNum, d.real_name AS realName, d.phone AS phone, d.operate_time AS operateTime, d.operate_name AS operateName FROM d_vehicle veh JOIN d_driver_vehicle_con con ON con.vehicle_id = veh.id JOIN d_driver d ON con.driver_id = d.id
con.plate_no = #{plateNo}
and d.real_name = #{realName}
and d.phone = #{phone}
and veh.ownership_type = 1 AND d.type = 2 AND d.state = 2
