mapper.select:
SELECT dv.id AS vehicleId, dd.id AS driverId, dv.plate_no AS plateNo, dv.default_carry_num AS defaultCarryNum, dd.real_name AS realName, dd.phone AS phone, dd.check_time AS checkTime, ba.NAME AS checkName FROM d_vehicle dv LEFT JOIN d_driver_vehicle_con ddvc ON ddvc.vehicle_id = dv.id LEFT JOIN d_driver dd ON dd.id = ddvc.driver_id LEFT JOIN b_admin ba ON ba.id = dd.check_user_id
ddvc.plate_no = #{plateNo}
and dd.real_name = #{realName}
and dd.phone = #{phone}
and dv.ownership_type = 1

SELECT id AS vehicleId, plate_no AS plateNo, default_carry_num AS defaultCarryNum FROM d_vehicle
plate_no like concat('%',#{plateNo},'%')
and ownership_type = 2 and carrier_id = #{carrierId}
