mapper.select:
SELECT veh.id AS vehicleId, d.id AS driverId, veh.plate_no AS plateNo, veh.default_carry_num AS defaultCarryNum, d.real_name AS realName, d.phone AS phone, d.check_time AS checkTime, admin.NAME AS checkName FROM d_vehicle veh LEFT JOIN d_driver_vehicle_con con ON con.vehicle_id = veh.id LEFT JOIN d_driver d ON d.id = con.driver_id LEFT JOIN b_admin admin ON admin.id = d.check_user_id
con.plate_no = #{plateNo}
and d.real_name = #{realName}
and d.phone = #{phone}
and veh.ownership_type = 1
